import React from 'react';
import ReactFlow, { MiniMap, Controls, Background, Node, Edge } from 'react-flow-renderer';
import { Step } from './models';

interface FlowViewProps {
  steps: Step[];
  selectedStepId?: string;
  stepsByPhase?: Record<string, Step[]>;
}

const nodeColors = [
  '#e3f2fd', // light blue
  '#ffe0b2', // light orange
  '#c8e6c9', // light green
  '#f8bbd0', // light pink
  '#fff9c4', // light yellow
  '#d1c4e9', // light purple
  '#b2dfdb', // light teal
];

const getNodesAndEdges = (steps: Step[], selectedStepId?: string): { nodes: Node[]; edges: Edge[] } => {
  const nodes: Node[] = steps.map((step, idx) => {
    const isSelected = step.id === selectedStepId;
    const baseColor = nodeColors[idx % nodeColors.length];
    return {
      id: step.id,
      data: {
        label: (
          <span style={{
            fontWeight: isSelected ? 800 : 500,
            color: isSelected ? '#ff9800' : '#1976d2',
            fontSize: isSelected ? 18 : 15,
            letterSpacing: 1,
          }}>{`${step.order}. ${step.type}`}</span>
        )
      },
      position: { x: 100, y: idx * 100 },
      style: {
        minWidth: 200,
        padding: 12,
        borderRadius: 10,
        border: isSelected
          ? '4px dashed #ff9800'
          : '2px solid #90caf9',
        background: isSelected ? '#fffbe6' : baseColor,
        boxShadow: isSelected ? '0 0 0 6px #ff980088' : '0 1px 4px #bbb8',
        animation: isSelected ? 'blinker 0.9s linear infinite' : undefined,
        transition: 'border 0.2s, box-shadow 0.2s, background 0.2s',
        zIndex: isSelected ? 10 : 1,
      },
    };
  });
  const edges: Edge[] = steps.slice(1).map((step, idx) => ({
    id: `e${steps[idx].id}-${step.id}`,
    source: steps[idx].id,
    target: step.id,
    animated: true,
    style: { stroke: '#1976d2', strokeWidth: 2 }
  }));
  return { nodes, edges };
};

const phaseColors = [
  'rgba(33,150,243,0.10)', // blue
  'rgba(255,152,0,0.10)', // orange
  'rgba(76,175,80,0.10)', // green
  'rgba(233,30,99,0.10)', // pink
  'rgba(255,235,59,0.15)', // yellow
  'rgba(156,39,176,0.10)', // purple
  'rgba(0,150,136,0.10)', // teal
];

const FlowView: React.FC<FlowViewProps> = ({ steps, selectedStepId, stepsByPhase }) => {
  // Grouping logic: if stepsByPhase provided, use it, else fall back to flat steps
  let grouped: Array<{ phase: string; steps: Step[] }> = [];
  if (stepsByPhase) {
    grouped = Object.entries(stepsByPhase)
      .sort(([a], [b]) => Number(a) - Number(b))
      .map(([phase, steps]) => ({ phase, steps }));
  } else {
    grouped = [{ phase: '1', steps }];
  }

  // Calculate nodes and edges for all steps
  const allSteps = grouped.flatMap(g => g.steps);
  const { nodes, edges } = getNodesAndEdges(allSteps, selectedStepId);

  // --- Ajustar rectángulos al layout real de nodos ---
  // Map: stepId -> node
  const nodeMap = Object.fromEntries(nodes.map(n => [n.id, n]));
  const phaseRects = grouped.map((g, idx) => {
    const phaseNodes = g.steps.map(s => nodeMap[s.id]).filter(Boolean);
    if (phaseNodes.length === 0) return null;
    // Calcular límites
    const minY = Math.min(...phaseNodes.map(n => n.position.y));
    const maxY = Math.max(...phaseNodes.map(n => n.position.y));
    const minX = Math.min(...phaseNodes.map(n => n.position.x));
    const maxX = Math.max(...phaseNodes.map(n => n.position.x));
    return {
      x: minX - 30,
      y: minY - 30,
      width: (maxX - minX) + 260,
      height: (maxY - minY) + 90,
      color: phaseColors[idx % phaseColors.length],
      label: `Fase ${g.phase}`,
      labelX: minX - 10,
      labelY: minY - 12
    };
  }).filter(Boolean);


  return (
    <div style={{ height: '60vh', minHeight: 400, width: '100%', background: '#f4f6fb', borderRadius: 8, position: 'relative' }}>
      <style>{`
        @keyframes blinker {
          50% { border-color: #fff; box-shadow: 0 0 0 8px #ff980044; }
        }
      `}</style>
      {/* SVG backgrounds for phases */}
      <svg
        width="100%"
        height="100%"
        style={{ position: 'absolute', left: 0, top: 0, zIndex: 0, pointerEvents: 'none' }}
      >
        {phaseRects.map((rect, idx) => (
          <g key={idx}>
            <rect
              x={30}
              y={rect.y + 30}
              width={320}
              height={rect.height}
              rx={24}
              fill={rect.color}
              stroke={rect.color.replace('0.10', '0.35')}
              strokeWidth={2}
            />
            <text
              x={40}
              y={rect.y + 52}
              fontSize={18}
              fontWeight={700}
              fill="#555"
              style={{ textShadow: '0 2px 8px #fff' }}
            >
              {rect.label}
            </text>
          </g>
        ))}
      </svg>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        fitView
        fitViewOptions={{ padding: 0.2 }}
        minZoom={0.2}
        maxZoom={2}
      >
        <MiniMap nodeColor={() => '#1976d2'} />
        <Controls />
        <Background gap={16} color="#e3e3e3" />
      </ReactFlow>
    </div>
  );
};

export default FlowView;
